import java.util.List;public class Tomb extends EventAbstract {    private Parser parser = new Parser("ArakanDialog.txt");    public Tomb(Player player) {        super(player);    }    public String getInfo() {        return "\n You are in the tomb of the Great Arakan.\n Answer questions or die...\n" +                "(for each wrong answer you get damage)";    }    public void execute() {        player.setCurrentEvent(this);        List<Question> listQuest = parser.toListQuestions();        for (int i = 0; i < listQuest.size(); i++) {            System.out.println(listQuest.get(i).question);            String answer = player.getAnswer();            if (listQuest.get(i).answers.contains(answer))                System.out.println("Good");            else {                System.out.println("No, mortal!!!");                player.getDamage(40);                if (!player.isAlive()) {                    System.out.println("you died");                    break;                }            }        }    }}