import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class Tomb extends EventAbstract {    public Player player;    private Parser parser = new Parser("ArakanDialog.txt");    private String eventName = "Tomb";    public String getEventName() {        return eventName;    }    public void execute(Player player) {        Scanner input = new Scanner(System.in);        List<Question> listQuest = null;        listQuest = parser.toListQuestions();        for (int i = 0; i < listQuest.size(); i++) {            System.out.println(listQuest.get(i).question);            String answer = input.next();            if (listQuest.get(i).answers.contains(answer))                System.out.println("Good");            else {                System.out.println("No, mortal!!!");                player.getDamage(40);                if (!player.isAlive()) {                    System.out.println("you died");                    break;                }            }        }    }}