public class eventGrobnica extends eventAbstract{    public Player player;    private Parser text;    private Bot bot;    private String eventName;        public String GetEventName()    {    	return eventName;    }    public eventGrobnica(Player pl) {        text = new Parser("diologs.txt");        eventName = "eventGrobnica";        this.bot = new Bot(this.text.toListQuestions());        player = pl;    }    public void Execute(){        while (this.player.IsAlive() && this.bot.NotEnd()){            System.out.println(bot.NextQuestion());            if (bot.IsTrueAnswer(player.getAnswer()))                System.out.println("right");            else{                System.out.println("false");                player.getDamage(20);            }        }    }}